def most_repeated_word(text):
    words = text.lower().split()
    word_counts = {}
    for word in words:
        if word not in word_counts:
            word_counts[word] = 0
        word_counts[word] += 1
    most_repeated = max(word_counts, key=word_counts.get)
    return most_repeated
