def mystic_number(n):
    """Returns 0 for n > 4, otherwise the next greatest multiple of 3 if not divisible by 3, or 0 if n > 4."""
    if n > 4:
        return 0
    if n % 3 == 0:
        return 0
    else:
        return (n // 3 + 1) * 3
