def combination_sum(nums, target):
    res = []
    def backtrack(path, start, total):
        if total == target:
            res.append(path)
            return
        if total > target:
            return
        for i in range(start, len(nums)):
            backtrack(path + [nums[i]], i + 1, total + nums[i])
    backtrack([], 0, 0)
    return res
