def caesar(text, shift, alphabet="abcdefghijklmnopqrstuvwxyz"):
    result = ''
    for char in text.lower():
        if char in alphabet:
            index = alphabet.find(char)
            new_index = (index + shift) % len(alphabet)
            result += alphabet[new_index]
        else:
            result += char
    return result
