def permutations(string):
    if len(string) == 0:
        return ['']
    result = []
    for i in range(len(string)):
        char = string[i]
        remaining_string = string[:i] + string[i+1:]
        for p in permutations(remaining_string):
            result.append(char + p)
    return result
