def permutations(n):
    if n == 0:
        return [[]]

    result = []
    for i in range(1, n + 1):
        smaller_permutations = permutations(n - 1)
        for perm in smaller_permutations:
            result.append([i] + [x if x < i else x for x in perm])
    return result
